import React, { Component } from "react";
import InstructionDataService from "../services/instruction.service";
import { Link } from "react-router-dom";

export default class InstructionList extends Component {
  constructor(props) {
    super(props);
    this.onChangeSearchInstruction = this.onChangeSearchInstruction.bind(this);
    this.retrieveInstruction = this.retrieveInstruction.bind(this);
    this.refreshList = this.refreshList.bind(this);
    this.setActiveInstruction = this.setActiveInstruction.bind(this);
    this.removeAllInstructions = this.removeAllInstructions.bind(this);
    this.searchDirection = this.searchDirection.bind(this);

    this.state = {
      instructions: [],
      currentInstruction: null,
      currentIndex: -1,
      searchDirection: ""
    };
  }

  componentDidMount() {
    this.retrieveInstruction();
  }

  onChangeSearchInstruction(e) {
    const searchDirection = e.target.value;

    this.setState({
      searchDirection: searchDirection
    });
  }

  retrieveInstruction() {
    InstructionDataService.getAll()
      .then(response => {
        this.setState({
          instructions: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  refreshList() {
    this.retrieveInstruction();
    this.setState({
      currentInstruction: null,
      currentIndex: -1
    });
  }

  setActiveInstruction(instruction, index) {
    this.setState({
      currentInstruction: instruction,
      currentIndex: index
    });
  }

  removeAllInstructions() {
    InstructionDataService.deleteAll()
      .then(response => {
        console.log(response.data);
        this.refreshList();
      })
      .catch(e => {
        console.log(e);
      });
  }

  searchDirection() {
    InstructionDataService.findByTitle(this.state.searchDirection)
      .then(response => {
        this.setState({
          instructions: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const { searchInstruction, instructions, currentInstruction, currentIndex } = this.state;

    return (
      <div className="list row">
        <div className="col-md-8">
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Search by direction"
              value={searchInstruction}
              onChange={this.onChangeSearchInstruction}
            />
            <div className="input-group-append">
              <button
                className="btn btn-outline-secondary"
                type="button"
                onClick={this.searchInstruction}
              >
                Search
              </button>
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <h4>Instructions List</h4>

          <ul className="list-group">
            {instructions &&
              instructions.map((instructions, index) => (
                <li
                  className={
                    "list-group-item " +
                    (index === currentIndex ? "active" : "")
                  }
                  onClick={() => this.setActiveInstruction(instructions, index)}
                  key={index}
                >
                  {instructions.title}
                </li>
              ))}
          </ul>

          <button
            className="m-3 btn btn-sm btn-danger"
            onClick={this.removeAllinstructions}
          >
            Remove All
          </button>
        </div>
        <div className="col-md-6">
          {currentInstruction ? (
            <div>
              <h4>Instructions</h4>
              <div>
                <label>
                  <strong>Direction:</strong>
                </label>{" "}
                {currentInstruction.direction}
              </div>
              <div>
                <label>
                  <strong>Obstacles:</strong>
                </label>{" "}
                {currentInstruction.obstacle}
              </div>
              <div>
                <label>
                  <strong>Status:</strong>
                </label>{" "}
                {currentInstruction.status ? "Complete" : "Pending"}
              </div>

              <Link
                to={"/instructions/" + currentInstruction.id}
                className="badge badge-warning"
              >
                Edit
              </Link>
            </div>
          ) : (
            <div>
              <br />
              <p>Please click on an instruction</p>
            </div>
          )}
        </div>
      </div>
    );
  }
}
