import React, { Component } from "react";
import { Switch, Route, Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";

import AddInstruction from "./components/add-instructions.component";
import InstructionList from "./components/instructions-list.component";
import Instruction from "./components/instruction.component";
import Home from "./components/home.component";

class App extends Component {
  render() {
    return (
      <div>
        <nav className="navbar navbar-expand navbar-dark bg-dark">
          <a href="/home" className="navbar-brand">
            Mars Rover
          </a>
          <div className="navbar-nav mr-auto">
            <li className="nav-item">
              <Link to={"/instructions"} className="nav-link">
                Instructions
              </Link>
            </li>
            <li className="nav-item">
              <Link to={"/control"} className="nav-link">
                Control
              </Link>
            </li>
          </div>
        </nav>

        <div className="container mt-3">
          <Switch>
            <Route exact path={["/", "/home"]} component={Home} />
            <Route exact path="/instructions" component={InstructionList} />
            <Route exact path="/add" component={AddInstruction} />
            <Route path="/instructions/:id" component={Instruction} />
          </Switch>
        </div>
      </div>
    );
  }
}



export default App;
