import React, { Component } from "react";
import InstructionDataService from "../services/instruction.service";

export default class Instruction extends Component {
  constructor(props) {
    super(props);
    this.onChangeInstruction = this.onChangeInstruction.bind(this);
    this.getInstruction = this.getInstruction.bind(this);
    this.updateCompletion = this.updateCompletion.bind(this);
    this.updateInstruction = this.updateInstruction.bind(this);
    this.deleteInstruction = this.deleteInstruction.bind(this);

    this.state = {
      currentInstruction: {
        id: null,
        title: "",
        description: "",
        published: false
      },
      message: ""
    };
  }

  componentDidMount() {
    this.getInstruction(this.props.match.params.id);
  }

  onChangeInstruction(e) {
    const title = e.target.value;

    this.setState(function(prevState) {
      return {
        currentInstruction: {
          ...prevState.currentInstruction,
          title: title
        }
      };
    });
  }

  onChangeDescription(e) {
    const description = e.target.value;

    this.setState(prevState => ({
      currentInstruction: {
        ...prevState.currentInstruction,
        description: description
      }
    }));
  }

  getInstruction(id) {
    InstructionDataService.get(id)
      .then(response => {
        this.setState({
          currentInstruction: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  updateCompletion(status) {
    var data = {
      id: this.state.currentInstruction.id,
      title: this.state.currentInstruction.title,
      description: this.state.currentInstruction.description,
      published: status
    };

    InstructionDataService.update(this.state.currentInstruction.id, data)
      .then(response => {
        this.setState(prevState => ({
          currentInstruction: {
            ...prevState.currentInstruction,
            published: status
          }
        }));
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  updateInstruction() {
    InstructionDataService.update(
      this.state.currentInstruction.id,
      this.state.currentInstruction
    )
      .then(response => {
        console.log(response.data);
        this.setState({
          message: "The tutorial was updated successfully!"
        });
      })
      .catch(e => {
        console.log(e);
      });
  }

  deleteInstruction() {    
    InstructionDataService.delete(this.state.currentInstruction.id)
      .then(response => {
        console.log(response.data);
        this.props.history.push('/tutorials')
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const { currentInstruction } = this.state;

    return (
      <div>
        {currentInstruction ? (
          <div className="edit-form">
            <h4>Tutorial</h4>
            <form>
              <div className="form-group">
                <label htmlFor="title">Title</label>
                <input
                  type="text"
                  className="form-control"
                  id="title"
                  value={currentInstruction.title}
                  onChange={this.onChangeInstruction}
                />
              </div>
              <div className="form-group">
                <label htmlFor="description">Description</label>
                <input
                  type="text"
                  className="form-control"
                  id="description"
                  value={currentInstruction.description}
                  onChange={this.onChangeDescription}
                />
              </div>

              <div className="form-group">
                <label>
                  <strong>Status:</strong>
                </label>
                {currentInstruction.published ? "Published" : "Pending"}
              </div>
            </form>

            {currentInstruction.published ? (
              <button
                className="badge badge-primary mr-2"
                onClick={() => this.updateCompletion(false)}
              >
                UnPublish
              </button>
            ) : (
              <button
                className="badge badge-primary mr-2"
                onClick={() => this.updateCompletion(true)}
              >
                Publish
              </button>
            )}

            <button
              className="badge badge-danger mr-2"
              onClick={this.deleteInstruction}
            >
              Delete
            </button>

            <button
              type="submit"
              className="badge badge-success"
              onClick={this.updateInstruction}
            >
              Update
            </button>
            <p>{this.state.message}</p>
          </div>
        ) : (
          <div>
            <br />
            <p>Please click on a Tutorial...</p>
          </div>
        )}
      </div>
    );
  }
}
