import React, { Component } from "react";
import InstructionDataService from "../services/instruction.service";

var mqtt = require('mqtt')
var client = mqtt.connect('ws://3.12.103.104', {clientID:"react_active"}, {username:"hs2119"}, {password:"marsrover"})
console.log("connected flag  " + client.connected);

var topic_list=["active"];
console.log("subscribing to topics");
client.subscribe(topic_list,{qos:1}); //topic list

export default class Instruction extends Component {
  constructor(props) {
    super(props);
    this.onChangeInstruction = this.onChangeInstruction.bind(this);
    this.onChangeAmount = this.onChangeAmount.bind(this);
    this.getInstruction = this.getInstruction.bind(this);
    this.updateCompleted = this.updateCompleted.bind(this);
    this.updateInstruction = this.updateInstruction.bind(this);
    this.deleteInstruction = this.deleteInstruction.bind(this);

    this.state = {
      currentInstruction: {
        id: null,
        instruction: "",
        amount: "",
        status: false
      },
      message: ""
    };
  }

  componentDidMount() {
    this.getInstruction(this.props.match.params.id);
  }

  onChangeInstruction(e) {
    const instruction = e.target.value;

    this.setState(function(prevState) {
      return {
        currentInstruction: {
          ...prevState.currentInstruction,
          instruction: instruction
        }
      };
    });
  }

  onChangeAmount(e) {
    const amount = e.target.value;

    this.setState(prevState => ({
      currentInstruction: {
        ...prevState.currentInstruction,
        amount: amount
      }
    }));
  }

  getInstruction(id) {
    InstructionDataService.get(id)
      .then(response => {
        this.setState({
          currentInstruction: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  updateCompleted(status) {
    //handle incoming messages
    client.on('message',function(topic, message, packet){
    console.log("message is "+ message);
    console.log("topic is "+ topic);

    var data = {
      id: this.state.currentInstruction.id,
      instruction: this.state.currentInstruction.instruction,
      amount: this.state.currentInstruction.amount,
      status: message
    }

    InstructionDataService.update(this.state.currentInstruction.id, data)
      .then(response => {
        this.setState(prevState => ({
          currentInstruction: {
            ...prevState.currentInstruction,
            status: status
          }
        }));
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
    })
  }

  updateInstruction() {
    InstructionDataService.update(
      this.state.currentInstruction.id,
      this.state.currentInstruction
    )
      .then(response => {
        console.log(response.data);
        this.setState({
          message: "The instruction was updated successfully!"
        });
      })
      .catch(e => {
        console.log(e);
      });
  }

  deleteInstruction() {    
    InstructionDataService.delete(this.state.currentInstruction.id)
      .then(response => {
        console.log(response.data);
        this.props.history.push('/instruction')
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const { currentInstruction } = this.state;

    return (
      <div>
        {currentInstruction ? (
          <div className="edit-form">
            <h4>Instruction</h4>
            <form>
              <div className="form-group">
                <label htmlFor="instruction">Instruction</label>
                <input
                  type="text"
                  className="form-control"
                  id="instruction"
                  value={currentInstruction.instruction}
                  onChange={this.onChangeInstruction}
                />
              </div>
              <div className="form-group">
                <label htmlFor="amount">Amount</label>
                <input
                  type="text"
                  className="form-control"
                  id="amount"
                  value={currentInstruction.amount}
                  onChange={this.onChangeAmount}
                />
              </div>

              <div className="form-group">
                <label>
                  <strong>Status:</strong>
                </label>
                {currentInstruction.status ? "Complete" : "Pending"}
              </div>
            </form>

            {currentInstruction.status ? (
              <button
                className="m-3 btn btn-sm btn-primary"
                onClick={() => this.updateCompleted()}
              >
                UnPublish
              </button>
            ) : (
              <button
                className="m-3 btn btn-sm btn-primary"
                onClick={() => this.updateCompleted()}
              >
                Publish
              </button>
            )}

            <button
              className="m-3 btn btn-sm btn-danger"
              onClick={this.deleteInstruction}
            >
              Delete
            </button>

            <button
              type="submit"
              className="m-3 btn btn-sm btn-success"
              onClick={this.updateInstruction}
            >
              Update
            </button>
            <p>{this.state.message}</p>
          </div>
        ) : (
          <div>
            <br />
            <p>Please click on an Instruction...</p>
          </div>
        )}
      </div>
    );
  }
}
