import {CanvasJSChart} from 'canvasjs-react-charts'
import React, { Component } from 'react';

var data = []
var pos = ['']
var updateInterval = 10;

var lastMsg
var lastTopic
var mqtt = require('mqtt')
var client = mqtt.connect("ws://18.117.97.5:9001", {clientId:"react_position", username:"hs2119", password:"marsrover"})
console.log("connected flag  " + client.connected);

var topic_list=["position","colour", "objectproximity", "objectcoordinates"];
console.log("subscribing to topics");
client.subscribe(topic_list,{qos:1}); //topic list

client.on("connect",function(){	
  console.log("connected  "+ client.connected);
  })


class Map extends Component {
	constructor() {
		super();
		this.updateChart = this.updateChart.bind(this);
		this.updateColour = this.updateColour.bind(this);
		this.updateProximity = this.updateProximity.bind(this);
		this.updateCoordinates = this.updateCoordinates.bind(this);
	}
	componentDidMount() {
		setInterval(this.updateChart, updateInterval);
	//	this.updateColour();
	//	this.updateProximity();
	//	this.updateCoordinates();
	}

	updateChart() {
		client.on('message',function(topic, message, packet){
      console.log("message is "+ message);
      console.log("topic is "+ topic);
      lastMsg = message.toString()
	  lastTopic = topic.toString()
	  console.log(lastTopic)
    })
	console.log(lastTopic)

	if (lastTopic == 'position'){
    	if (lastMsg != null && lastMsg != pos[pos.length - 1] && this.chart != null ){
    		pos.push(lastMsg)
      		console.log("diff value"+ lastMsg + " " + pos[pos.length - 1])

      		var coords_str = lastMsg.split(',')
      		var x = parseFloat(coords_str[0]);
      		var y = parseFloat(coords_str[1]);
      		data.push({x: x,y: y});
      		console.log(data)
			  console.log(this.chart)
      		this.chart.render();
    } }

	else {
		if (lastTopic == 'colour'){
		console.log('im here')
	  	return lastMsg
		}
		else{
			if (lastTopic == 'objectproximity'){
				return lastMsg
			  }
			  else{
				if (lastTopic == 'objectcoordinates'){
					return lastMsg
			  }
			}
		}
	}  

	}

	updateColour() {
		client.on('message',function(topic, message, packet){
			console.log("message is "+ message);
			console.log("topic is "+ topic);
			console.log("im here")
			console.log(message.toString())
			lastMsg = message.toString()
			lastTopic = topic.toString()

		  })

		  if (lastTopic == 'colour'){
			  console.log('im here')
			return lastMsg
		  }
	}


	updateProximity() {
		client.on('message',function(topic, message, packet){
			console.log("message is "+ message);
			console.log("topic is "+ topic);
			lastMsg = message.toString()
			lastTopic = topic.toString()
			console.log(lastMsg)
		  })

		  if (lastTopic == 'objectproximity'){
			return lastMsg
		  }
	}

	updateCoordinates() {
		client.on('message',function(topic, message, packet){
			console.log("message is "+ message);
			console.log("topic is "+ topic);
			lastMsg = message.toString()
			lastTopic = topic.toString()
		  })

		  if (lastTopic == 'objectcoordinates'){
			return lastMsg
		  }
	}

	render() {
		const options = {
			title :{
				text: "Mars Rover Plot"
			},
			data: [{
				type: "line",
				dataPoints : data
			}]
		}
		console.log(this.updateColour())
		return (
		<div>
			<CanvasJSChart options = {options}
				 onRef={ref => this.chart = ref}
			/>
			${this.updateColour()}
			{/*You can get reference to the chart instance as shown above using onRef. This allows you to access all chart properties and methods*/}
			<h4>An object coloured ${this.updateChart()} has been detected with position {this.updateCoordinates()}, the object is {this.updateProximity} </h4>
		</div>
		);
	}
}
export default Map;
