import React, { Component } from "react"
import InstructionDataService from "../services/instruction.service"
import { Link } from "react-router-dom";

export default class AddInstruction extends Component {
  constructor(props) {
    super(props);
    this.onChangeDirection = this.onChangeDirection.bind(this);
    this.saveInstruction = this.saveInstruction.bind(this);
    this.newInstruction = this.newInstruction.bind(this);

    this.state = {
      id: null,
      direction: "",
      obstacle: false,
      status: "",

      submitted: false
    };
  }

  onChangeDirection(e) {
    this.setState({
      Direction: e.target.value
    });
  }

  saveInstruction() {
    var data = {
      Direction: this.state.Direction,
    };

    InstructionDataService.create(data)
      .then(response => {
        this.setState({
          id: response.data.id,
          direction: response.data.direction,
          obstacle: response.data.obstacle,
          status: response.data.status,

          submitted: true
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  newInstruction() {
    this.setState({
      id: null,
      Direction: "",
      Obstacle: false,
      Status: "",

      submitted: false
    });
  }

  render() {
    return (
      <div className="submit-form">
        {this.state.submitted ? (
          <div>
            <h4>You submitted successfully!</h4>
            <button className="btn btn-success" onClick={this.newInstruction}>
              Add
            </button>
          </div>
        ) : (
          <div>
            <div className="form-group">
              <label htmlFor="direction">Direction</label>
            <input
                type="text"
                className="form-control"
                id="direction"
                required
                value={this.state.direction}
                onChange={this.onChangeDirection}
                name="direction"
              />
            </div>

            <button onClick={this.saveInstruction} className="btn btn-success">
              Submit
            </button>
          </div>
        )}
      </div>
    );
  }
}
