import React, { Component } from "react";
import InstructionDataService from "../services/Instruction.service";
import { Link } from "react-router-dom";

export default class InstructionsList extends Component {
  constructor(props) {
    super(props);
    this.onChangeSearchTitle = this.onChangeSearchTitle.bind(this);
    this.retrieveInstructions = this.retrieveInstructions.bind(this);
    this.refreshList = this.refreshList.bind(this);
    this.setActiveInstruction = this.setActiveInstruction.bind(this);
    this.removeAllInstructions = this.removeAllInstructions.bind(this);
    this.searchTitle = this.searchTitle.bind(this);

    this.state = {
      instructions: [],
      currentInstruction: null,
      currentIndex: -1,
      searchTitle: ""
    };
  }

  componentDidMount() {
    this.retrieveInstructions();
  }

  onChangeSearchTitle(e) {
    const searchTitle = e.target.value;

    this.setState({
      searchTitle: searchTitle
    });
  }

  retrieveInstructions() {
    InstructionDataService.getAll()
      .then(response => {
        this.setState({
          instructions: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  refreshList() {
    this.retrieveInstructions();
    this.setState({
      currentInstruction: null,
      currentIndex: -1
    });
  }

  setActiveInstruction(instruction, index) {
    this.setState({
      currentInstruction: instruction,
      currentIndex: index
    });
  }

  removeAllInstructions() {
    InstructionDataService.deleteAll()
      .then(response => {
        console.log(response.data);
        this.refreshList();
      })
      .catch(e => {
        console.log(e);
      });
  }

  searchTitle() {
    this.setState({
      currentInstruction: null,
      currentIndex: -1
    });

    InstructionDataService.findByTitle(this.state.searchTitle)
      .then(response => {
        this.setState({
          instructions: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const { searchTitle, instructions, currentInstruction, currentIndex } = this.state;

    return (
      <div className="list row">
        <div className="col-md-8">
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Search by title"
              value={searchTitle}
              onChange={this.onChangeSearchTitle}
            />
            <div className="input-group-append">
              <button
                className="btn btn-outline-secondary"
                type="button"
                onClick={this.searchTitle}
              >
                Search
              </button>
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <h4>Instructions List</h4>

          <ul className="list-group">
            {instructions &&
              instructions.map((instruction, index) => (
                <li
                  className={
                    "list-group-item " +
                    (index === currentIndex ? "active" : "")
                  }
                  onClick={() => this.setActiveInstruction(instruction, index)}
                  key={index}
                >
                  {instruction.title}
                </li>
              ))}
          </ul>

          <button
            className="m-3 btn btn-sm btn-danger"
            onClick={this.removeAllInstructions}
          >
            Remove All
          </button>
        </div>
        <div className="col-md-6">
          {currentInstruction ? (
            <div>
              <h4>Instruction</h4>
              <div>
                <label>
                  <strong>Title:</strong>
                </label>{" "}
                {currentInstruction.title}
              </div>
              <div>
                <label>
                  <strong>Description:</strong>
                </label>{" "}
                {currentInstruction.description}
              </div>
              <div>
                <label>
                  <strong>Status:</strong>
                </label>{" "}
                {currentInstruction.published ? "Published" : "Pending"}
              </div>

              <Link
                to={"/instructions/" + currentInstruction.id}
                className="badge badge-warning"
              >
                Edit
              </Link>
            </div>
          ) : (
            <div>
              <br />
              <p>Please click on a Instruction...</p>
            </div>
          )}
        </div>
      </div>
    );
  }
}
